package com.fluxtion.dataflow.runtime.audit;

/**
 * A target for a {@link LogRecord} generated by the {@link EventLogManager}. A
 * LogRecordListener is responsible for publishing the {@link LogRecord}.
 *
 * @author Greg Higgins (greg.higgins@v12technology.com)
 */
public interface LogRecordListener {

    void processLogRecord(LogRecord logRecord);
}
